mapper.select:
SELECT
CORRNO, CR.STUDNO AS STUDNO, S.NAME AS STUDNAME, CR.COURSNO AS COURSNO, CO.NAME AS COURSNAME, CR.PROFNO AS PROFNO, REASON, CR.STATUS corrStatus, CR.REGDATE AS REGDATE
FROM TBL_CORRECTION CR
JOIN TBL_COURSE CO
ON CR.COURSNO = CO.COURSNO
JOIN TBL_STUDENT S
ON CR.STUDNO = S.STUDNO


	CR.STUDNO = #{studno}


	AND CR.COURSNO = #{coursno}


	AND S.DEPTNO = #{deptno}


	AND CR.STATUS = #{status}


	AND CR.PROFNO = #{profno}


order by corrno desc

mapper.update:
UPDATE TBL_CORRECTION
SET
	REASON = #{reason},
	STATUS = #{corrStatus}
WHERE CORRNO = #{corrno}
