mapper.select:
    	SELECT  c.coursno, c.name, c.credit, c.maxnum, c.semester semester, c.starttime, c.endtime, c.day, c.profno,  p.name pname, c.deptno, d.name dname, c.locno, c.enable
		FROM tbl_course c, tbl_professor p, tbl_department d, tbl_location l
		WHERE c.deptno = d.deptno
		AND c.profno = p.profno
		AND c.locno = l.locno


				c.enable = #{enable}


				AND c.coursno = #{coursno}


				AND c.name LIKE '%'||#{cname}||'%'


				AND c.profno = #{profno}


				AND p.name LIKE '%'||#{pname}||'%'


				AND mod(c.semester, 2) = #{semester}


				AND c.deptno = #{deptno}


				AND to_char(c.regdate, 'YYYY') = #{year}

    	SELECT  c.coursno, c.name, c.credit, c.maxnum, c.semester semester, c.starttime, c.endtime, c.day, c.profno,  p.name pname, c.deptno, d.name dname, c.locno, c.enable
		FROM tbl_course c 
		join tbl_professor p
		on c.profno = p.profno
		join tbl_department d
		on c.deptno = d.deptno
		join tbl_location l
		on c.locno = l.locno


				c.enable = #{enable}


				AND c.coursno = #{coursno}


				AND c.name LIKE '%'||#{cname}||'%'


				AND c.profno = #{profno}


				AND p.name LIKE '%'||#{pname}||'%'


				AND mod(c.semester, 2) = #{semester}


				AND c.deptno = #{deptno}


				AND to_char(c.regdate, 'YYYY') = #{year}

SELECT MAX(COURSNO)
FROM TBL_COURSE
WHERE DEPTNO = #{deptno}

SELECT LOCNO
FROM TBL_LOCATION
WHERE DEPTNO = #{deptno}
