mapper.select:
    	SELECT
   			S.STUDNO, S.NAME AS NAME, PASSWORD, S.BIRTHDATE, S.ADDRESS, S.TEL, GRADE, S.GENDER, FEATURE, STATUS, S.DEPTNO, D.NAME AS DEPTNAME, S.PROFNO, P.NAME AS PROFNAME, S.EMAIL AS EMAIL
		FROM TBL_STUDENT S
		JOIN TBL_DEPARTMENT D
		ON S.DEPTNO = D.DEPTNO
		LEFT JOIN TBL_PROFESSOR P
		ON S.PROFNO = P.PROFNO
		JOIN TBL_STUD_ACCOUNT A
		ON S.STUDNO = A.STUDNO


			S.GRADE = #{grade}


			AND S.GENDER = #{gender}


			AND S.FEATURE = #{feature}


			AND S.STATUS = #{status}


			AND S.DEPTNO = #{deptno}


			AND S.PROFNO = #{profno}

mapper.update:
UPDATE TBL_MEMBER
SET
	USERPW = #{userpw}
WHERE USERID = #{userid}
